import{_ as i,c as n,o as h,ae as a}from"./chunks/framework.BtEkjhVd.js";const u=JSON.parse('{"title":"Giới thiệu Playwright","description":"","frontmatter":{"title":"Giới thiệu Playwright"},"headers":[],"relativePath":"docs/playwright/index.md","filePath":"docs/playwright/index.md"}'),r={name:"docs/playwright/index.md"};function l(o,t,e,c,g,s){return h(),n("div",null,t[0]||(t[0]=[a('<h1 id="🧪-gioi-thieu-ve-kiem-thu-tu-đong-voi-playwright" tabindex="-1">🧪 Giới thiệu về kiểm thử tự động với Playwright <a class="header-anchor" href="#🧪-gioi-thieu-ve-kiem-thu-tu-đong-voi-playwright" aria-label="Permalink to &quot;🧪 Giới thiệu về kiểm thử tự động với Playwright&quot;">​</a></h1><h2 id="🎭-playwright-la-gi" tabindex="-1">🎭 Playwright là gì? <a class="header-anchor" href="#🎭-playwright-la-gi" aria-label="Permalink to &quot;🎭 Playwright là gì?&quot;">​</a></h2><p><strong>Playwright</strong> là một công cụ giúp bạn <strong>tự động mở trang web, bấm nút, nhập dữ liệu, và kiểm tra xem mọi thứ có hoạt động đúng không</strong> — giống như một người dùng thực sự.</p><blockquote><p>🧠 Hãy tưởng tượng bạn có một “robot” mở website, làm thao tác thay bạn, và báo lại nếu có gì sai.</p></blockquote><h2 id="🤖-tai-sao-can-kiem-thu-tu-đong" tabindex="-1">🤖 Tại sao cần kiểm thử tự động? <a class="header-anchor" href="#🤖-tai-sao-can-kiem-thu-tu-đong" aria-label="Permalink to &quot;🤖 Tại sao cần kiểm thử tự động?&quot;">​</a></h2><ul><li>✅ Tiết kiệm thời gian (không cần kiểm tra thủ công từng lần).</li><li>✅ Tránh bỏ sót lỗi (máy làm thì không mệt mỏi).</li><li>✅ Lặp lại được nhiều lần (mỗi lần ra phiên bản mới đều có thể test lại tự động).</li></ul><hr><h2 id="⚔️-so-sanh-playwright-voi-cac-cong-cu-khac" tabindex="-1">⚔️ So sánh Playwright với các công cụ khác? <a class="header-anchor" href="#⚔️-so-sanh-playwright-voi-cac-cong-cu-khac" aria-label="Permalink to &quot;⚔️ So sánh Playwright với các công cụ khác?&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Công cụ</th><th>Ưu điểm chính</th><th>Nhược điểm chính</th></tr></thead><tbody><tr><td><strong>Selenium</strong></td><td>Phổ biến, hỗ trợ nhiều trình duyệt</td><td>Cài đặt phức tạp, chạy chậm</td></tr><tr><td><strong>Cypress</strong></td><td>Dễ dùng, tài liệu tốt</td><td>Chạy tốt với Chrome, hạn chế trình duyệt</td></tr><tr><td><strong>Playwright</strong></td><td>Dễ dùng, hỗ trợ nhiều trình duyệt, nhanh</td><td>Còn mới hơn nên tài liệu ít hơn Selenium</td></tr></tbody></table><hr><h2 id="📌-vi-sao-ban-nen-chon-playwright" tabindex="-1">📌 Vì sao bạn nên chọn Playwright? <a class="header-anchor" href="#📌-vi-sao-ban-nen-chon-playwright" aria-label="Permalink to &quot;📌 Vì sao bạn nên chọn Playwright?&quot;">​</a></h2><ul><li>✅ <strong>Hỗ trợ Chrome, Firefox, Safari</strong> chỉ với một dòng lệnh.</li><li>✅ <strong>Rất dễ cài đặt</strong>, chạy ngay được.</li><li>✅ <strong>Có công cụ tạo test tự động</strong> (chỉ cần bấm và thao tác như người dùng).</li><li>✅ <strong>Hỗ trợ AI rất tốt</strong> – bạn có thể viết test bằng cách mô tả bằng tiếng Việt!</li></ul><blockquote><p>💬 Ví dụ: &quot;Tôi muốn kiểm tra xem khi nhập sai mật khẩu thì sẽ hiện thông báo lỗi.&quot; → ChatGPT sẽ viết test Playwright cho bạn!</p></blockquote><hr><h2 id="👶-ai-nen-đoc-tai-lieu-nay" tabindex="-1">👶 Ai nên đọc tài liệu này? <a class="header-anchor" href="#👶-ai-nen-đoc-tai-lieu-nay" aria-label="Permalink to &quot;👶 Ai nên đọc tài liệu này?&quot;">​</a></h2><ul><li>Bạn làm kiểm thử phần mềm (tester) nhưng chưa biết lập trình.</li><li>Bạn muốn kiểm tra giao diện web mà không phụ thuộc lập trình viên.</li><li>Bạn tò mò về tự động hóa test, nhưng sợ code.</li></ul><h2 id="🧰-toi-can-chuan-bi-nhung-gi" tabindex="-1">🧰 Tôi cần chuẩn bị những gì? <a class="header-anchor" href="#🧰-toi-can-chuan-bi-nhung-gi" aria-label="Permalink to &quot;🧰 Tôi cần chuẩn bị những gì?&quot;">​</a></h2><ul><li>Máy tính đã cài Node.js (bạn sẽ được hướng dẫn cụ thể)</li><li><strong>Một công cụ để chạy lệnh</strong>: Terminal / Command Prompt / Terminal tích hợp trong IDE</li><li>✅ <strong>Khuyên dùng</strong>: <a href="https://www.jetbrains.com/webstorm/" target="_blank" rel="noreferrer">WebStorm</a> – nay đã miễn phí cho mọi người!</li></ul><div class="tip custom-block"><p class="custom-block-title">Tại sao nên dùng WebStorm?</p><ul><li>WebStorm có gợi ý thông minh khi viết test</li><li>Có sẵn terminal tích hợp</li><li>Tự kiểm tra lỗi khi viết sai cú pháp</li><li>Hỗ trợ cực tốt cho Playwright và TypeScript</li></ul></div><hr><blockquote><p>💡 Bạn vẫn có thể dùng VSCode nếu quen, nhưng WebStorm hiện đã miễn phí và rất mạnh mẽ cho người mới học.</p></blockquote><blockquote><p>✅ Đừng lo nếu bạn chưa từng viết dòng mã nào — mọi bước sẽ được hướng dẫn rất chi tiết và có thể copy-paste là chạy!</p></blockquote><p>➡️ Tiếp theo: <a href="./setup.html">Cài đặt môi trường để viết test với Playwright</a></p>',23)]))}const d=i(r,[["render",l]]);export{u as __pageData,d as default};
